mapper.select:
SELECT COUNT(PIN_NO) AS COUNT
FROM USERS
WHERE ID=#{id} AND PW=#{pw}

SELECT PIN_NO AS PINNO
FROM USERS
WHERE ID=#{id} AND PW=#{pw}

SELECT COUNT(PIN_NO) AS COUNT
FROM ADMIN
WHERE ADMIN_ID=#{id} AND ADMIN_PW=#{pw}

SELECT PIN_NO AS PINNO
FROM ADMIN
WHERE ADMIN_ID=#{id} AND ADMIN_PW=#{pw}

SELECT ADMIN_NO AS ADMINNO
FROM ADMIN
WHERE ADMIN_ID=#{id} AND ADMIN_PW=#{pw}
