mapper.select:
SELECT PRJ.PRJ_NO AS PRJNO, REC.RECRUIT_NO AS RECRUITNO, REC.TITLE AS RECRUITNAME, TO_CHAR(REC.PRJ_START) AS PRJSTART, TO_CHAR(REC.PRJ_END) AS PRJEND, POS.POS_NAME AS POSNAME , TO_CHAR(TRUNC(REC.PRJ_END - SYSDATE)) AS DDAY FROM PROJECT PRJ LEFT JOIN RECRUIT REC ON PRJ.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN RECRUIT_POS RP ON REC.RECRUIT_NO = RP.RECRUIT_NO LEFT JOIN APPLY AP ON RP.RECRUIT_POS_NO = AP.RECRUIT_POS_NO LEFT JOIN POSITION POS ON POS.POS_NO = RP.POS_NO WHERE AP.PIN_NO = #{pinNo} AND PRJ.PRJ_NO NOT IN ( SELECT DISTINCT PRJ_NO FROM ( SELECT PRJ.PRJ_NO FROM MEMBER_OUT MO LEFT JOIN FINAL FN ON MO.FINAL_NO = FN.FINAL_NO LEFT JOIN FIRST_CK FS ON FN.FIRST_CK_NO = FS.FIRST_CK_NO LEFT JOIN APPLY AP ON FS.APPLY_NO = AP.APPLY_NO LEFT JOIN RECRUIT_POS RP ON AP.RECRUIT_POS_NO = RP.RECRUIT_POS_NO LEFT JOIN RECRUIT REC ON RP.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN PROJECT PRJ ON REC.RECRUIT_NO = PRJ.RECRUIT_NO WHERE AP.PIN_NO = #{pinNo}
UNION
SELECT PS.PRJ_NO FROM PROJECT_STOP PS LEFT JOIN PROJECT PRJ ON PS.PRJ_NO = PRJ.PRJ_NO LEFT JOIN RECRUIT REC ON PRJ.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN RECRUIT_POS RP ON REC.RECRUIT_NO = RP.RECRUIT_NO LEFT JOIN APPLY AP ON RP.RECRUIT_POS_NO = AP.RECRUIT_POS_NO WHERE AP.PIN_NO = #{pinNo}
UNION
SELECT PRJ.PRJ_NO FROM PROJECT PRJ LEFT JOIN RECRUIT REC ON PRJ.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN RECRUIT_POS RP ON REC.RECRUIT_NO = RP.RECRUIT_NO LEFT JOIN APPLY AP ON RP.RECRUIT_POS_NO = AP.RECRUIT_POS_NO WHERE SYSDATE > REC.PRJ_END AND AP.PIN_NO = #{pinNo} ) )

SELECT DISTINCT PRJ_NO AS PRJNO, RECRUIT_NO AS RECRUITNO, TITLE AS RECRUITNAME, PRJ_START AS PRJSTART, PRJ_END AS PRJEND, POS_NAME AS POSNAME FROM ( SELECT PRJ.PRJ_NO, REC.RECRUIT_NO, REC.TITLE, REC.PRJ_START, REC.PRJ_END, POS.POS_NAME FROM MEMBER_OUT MO LEFT JOIN FINAL FN ON MO.FINAL_NO = FN.FINAL_NO LEFT JOIN FIRST_CK FS ON FN.FIRST_CK_NO = FS.FIRST_CK_NO LEFT JOIN APPLY AP ON FS.APPLY_NO = AP.APPLY_NO LEFT JOIN RECRUIT_POS RP ON AP.RECRUIT_POS_NO = RP.RECRUIT_POS_NO LEFT JOIN RECRUIT REC ON RP.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN PROJECT PRJ ON REC.RECRUIT_NO = PRJ.RECRUIT_NO LEFT JOIN POSITION POS ON POS.POS_NO = RP.POS_NO WHERE AP.PIN_NO = #{pinNo}
UNION
SELECT PS.PRJ_NO, REC.RECRUIT_NO, REC.TITLE, REC.PRJ_START, REC.PRJ_END, POS.POS_NAME FROM PROJECT_STOP PS LEFT JOIN PROJECT PRJ ON PS.PRJ_NO = PRJ.PRJ_NO LEFT JOIN RECRUIT REC ON PRJ.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN RECRUIT_POS RP ON REC.RECRUIT_NO = RP.RECRUIT_NO LEFT JOIN APPLY AP ON RP.RECRUIT_POS_NO = AP.RECRUIT_POS_NO LEFT JOIN POSITION POS ON POS.POS_NO = RP.POS_NO WHERE AP.PIN_NO = #{pinNo}
UNION
SELECT PRJ.PRJ_NO, REC.RECRUIT_NO, REC.TITLE, REC.PRJ_START, REC.PRJ_END, POS.POS_NAME FROM PROJECT PRJ LEFT JOIN RECRUIT REC ON PRJ.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN RECRUIT_POS RP ON REC.RECRUIT_NO = RP.RECRUIT_NO LEFT JOIN APPLY AP ON RP.RECRUIT_POS_NO = AP.RECRUIT_POS_NO LEFT JOIN POSITION POS ON POS.POS_NO = RP.POS_NO WHERE SYSDATE > REC.PRJ_END AND AP.PIN_NO = #{pinNo} ) COMBINED_RESULTS ORDER BY PRJ_END DESC

SELECT COUNT(*) AS COUNT FROM PROJECT PRJ LEFT JOIN RECRUIT REC ON PRJ.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN RECRUIT_POS RP ON REC.RECRUIT_NO = RP.RECRUIT_NO LEFT JOIN APPLY AP ON RP.RECRUIT_POS_NO = AP.RECRUIT_POS_NO LEFT JOIN POSITION POS ON POS.POS_NO = RP.POS_NO WHERE AP.PIN_NO = #{pinNo} AND PRJ.PRJ_NO NOT IN ( SELECT DISTINCT PRJ_NO FROM ( SELECT PRJ.PRJ_NO FROM MEMBER_OUT MO LEFT JOIN FINAL FN ON MO.FINAL_NO = FN.FINAL_NO LEFT JOIN FIRST_CK FS ON FN.FIRST_CK_NO = FS.FIRST_CK_NO LEFT JOIN APPLY AP ON FS.APPLY_NO = AP.APPLY_NO LEFT JOIN RECRUIT_POS RP ON AP.RECRUIT_POS_NO = RP.RECRUIT_POS_NO LEFT JOIN RECRUIT REC ON RP.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN PROJECT PRJ ON REC.RECRUIT_NO = PRJ.RECRUIT_NO WHERE AP.PIN_NO = #{pinNo}
UNION
SELECT PS.PRJ_NO FROM PROJECT_STOP PS LEFT JOIN PROJECT PRJ ON PS.PRJ_NO = PRJ.PRJ_NO LEFT JOIN RECRUIT REC ON PRJ.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN RECRUIT_POS RP ON REC.RECRUIT_NO = RP.RECRUIT_NO LEFT JOIN APPLY AP ON RP.RECRUIT_POS_NO = AP.RECRUIT_POS_NO WHERE AP.PIN_NO = #{pinNo}
UNION
SELECT PRJ.PRJ_NO FROM PROJECT PRJ LEFT JOIN RECRUIT REC ON PRJ.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN RECRUIT_POS RP ON REC.RECRUIT_NO = RP.RECRUIT_NO LEFT JOIN APPLY AP ON RP.RECRUIT_POS_NO = AP.RECRUIT_POS_NO WHERE SYSDATE > REC.PRJ_END AND AP.PIN_NO = #{pinNo} ) )

SELECT DISTINCT COUNT(*) AS COUNT FROM ( SELECT PRJ.PRJ_NO, REC.RECRUIT_NO, REC.TITLE, REC.PRJ_START, REC.PRJ_END, POS.POS_NAME FROM MEMBER_OUT MO LEFT JOIN FINAL FN ON MO.FINAL_NO = FN.FINAL_NO LEFT JOIN FIRST_CK FS ON FN.FIRST_CK_NO = FS.FIRST_CK_NO LEFT JOIN APPLY AP ON FS.APPLY_NO = AP.APPLY_NO LEFT JOIN RECRUIT_POS RP ON AP.RECRUIT_POS_NO = RP.RECRUIT_POS_NO LEFT JOIN RECRUIT REC ON RP.RECRUIT_NO = REC.RECRUIT_NO LEFT JOIN PROJECT PRJ ON REC.RECRUIT_NO = PRJ.RECRUIT_NO LEFT JOIN POSITION POS ON POS.POS_NO = RP.POS_NO WHERE AP.PIN_NO = #{pinNo}
