mapper.select:
SELECT PROFILENO,NICKNAME,ID,REGIONNO,REGIONNAME,SUBREGIONNO,SUBREGIONNAME,POSNO,POSNAME, PHOTOURL
FROM PROFILEVIEW
WHERE PINNO=#{pinNo}

SELECT TOOLNAME
FROM USER_TOOL_VIEW
WHERE PINNO=#{pinNo}

SELECT COUNT(*) AS RESULT
FROM PROFILE
WHERE PIN_NO=#{pinNo}

mapper.update:
UPDATE PROFILE
SET  POS_NO = #{posNo}
   , SUB_REGION_NO = #{subRegionNo}
   , PROFILE_DATE = SYSDATE
WHERE PIN_NO = #{pinNo}
