mapper.select:

SELECT WORKSPACE_NO, TITLE, WORK_DATE, CREATED_DATE, CONTENT
FROM WORKSPACE 
WHERE WORKSPACE_NO = #{workspaceNo}

mapper.insert:
INSERT INTO WORKSPACE(WORKSPACE_NO, FINAL_NO, TITLE, CONTENT, WORK_DATE, CREATED_DATE)
VALUES('WS'||TO_CHAR(WORKSPACENOSEQ.NEXTVAL) 
     , 'FN1'  
     , #{title}
     , #{content}
     , #{workDate}
     , SYSDATE)

mapper.update:
UPDATE WORKSPACE
SET TITLE = #{title},
    CONTENT =  #{content},
    WORK_DATE = #{workDate}
WHERE WORKSPACE_NO = #{workspaceNo}
