mapper.select:
SELECT COUNT(*) AS COUNT
FROM IDCHECK
WHERE ID=#{userId}

SELECT COUNT(*) AS COUNT
FROM USERS
WHERE NICKNAME=#{nickname}

SELECT PHOTOURL
FROM USERS
WHERE PIN_NO=#{pinNo}

mapper.update:
UPDATE USERS
SET PHOTOURL='images/defaulfPhoto.jpg'
WHERE PIN_NO=${pinNo}

UPDATE USERS
SET PHOTOURL=${photourl}
WHERE PIN_NO=${pinNo}
